'start_link'() -> 'Tianjiupai.UserResourceServer.Impl':'start_link_name'(ok, {'global', ok}).
'as_pid'(S2634Proc) -> 'Tianjiupai.UserResourceServer.Impl':'as_pid'(S2634Proc).
